// import React, { useState} from "react";
import ExpenseItem from "./ExpenseItem.js";
import "./Expenses.css";
import Card from "../UI/Card.js";
import React, { useState } from "react";
import ExpensesFilter from "../Filter/ExpensesFilter.js";

function Expenses(props) {
  const [filterYear, setFilterYear] = useState("2023");
  // const [filterExpenses, setFilterExpenses] = useState(props.expenses);
  let filterExpenses = props.expenses;

  console.log(filterExpenses);
  const onFilter = (expense) => {
    return expense.date.getFullYear() == filterYear;
  };
  const filterChangeHandler = (selectedYear) => {

    setFilterYear(selectedYear);
    filterExpenses=props.expenses.filter((expense) => {
        return expense.date.getFullYear() == selectedYear;
      })
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filterYear}
          onChangeFilter={filterChangeHandler}
        ></ExpensesFilter>
        
        {filterExpenses.map((ourExpenses) => (
          <ExpenseItem key={ourExpenses.id} expense={ourExpenses} />
        ))}
      </Card>
    </div>
  );
}
export default Expenses;
