 function ExpenseItem(props){       
 // function ExpenseItem(date,title,amount){                       //This is 2nd alternative in which we just remove props.expenses from below code and it works same.This is proper javascript object destructing concept

  const day=props.expenses.date.toLocaleString('en-US', { day: '2-digit'});
  const month=props.expenses.date.toLocaleString('en-US', { month: 'long'});
  const year=props.expenses.date.getFullYear();
    return(
        <div className="expense-item">
      <div>
        <div>{month}</div>
        <div>{year}</div>
        <div>{day}</div>
      </div>
      <div className="expense-item__description">
        <h2>{props.expenses.title}</h2>
        <div className="expense-item__price">${props.expenses.amount}</div>
      </div>
    </div>
    )
}
export default ExpenseItem;
