import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";
import React from 'react';
// import React, {useState} from "react";


function App() {
  const expenses = [
    {
      id: "e1",
      title: "Toilet Paper",
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: "e3",
      title: "Car Insurance",
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: "e4",
      title: "New Desk (Wooden)",
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];
  const [enteredDate, setEnteredDate]= useState(expenses);
  const addExpenseHandler= expense =>{
    console.log("This is Apps.js");
    console.log(expense);
    expenses.push(expense);
    setEnteredDate(expenses);
    console.log(expenses);
  }

  return (
    <div>
      <NewExpense onAddingExpense={addExpenseHandler}/>
      {/* <ExpenseItem                     //This is first alternative ways also see in expenseItem.js file this apply to all ExpenseItem in this file (means below)
        title={expenses[0].title}
        amount={expenses[0].amount}
        date={expenses[0].date}
      ></ExpenseItem> */}
       {/* <Expenses expenses={expenses}></Expenses> */}
       <Expenses expenses={enteredDate}></Expenses>
    </div>
  );
}

export default App;
