    const totalMaximum=Math.max(...dataPointValues);
    console.log(dataPointValues);

    return (
        <div className='chart'>
            {/* {props.dataPoints.map=((datapoint)=>{    //Error in this map or after return 
                <ChartBar
                 key={datapoint.label} 
                 value={datapoint.value} 
                 maxValue={totalMaximum} 
                 label={datapoint.label}
                 />
            })} */}

            <ChartBar
            key={props.dataPoints[0].label} 
            value={props.dataPoints[0].value} 
            maxValue={totalMaximum} 
            label={props.dataPoints[0].label}
            />


            
            {/* <AddExpenses /> */}
        </div>
    )
}
export default chart;
