import './ExpensesList.css'
import ExpenseItem from "./ExpenseItem.js";
// import ExpenseItem from "./ExpenseItem.js";

function ExpensesList(props){
    // if(props.expenses.length==0){
    //     return (<h2 className='expenses-list__fallback'>No Expenses Found</h2>)
    // }
    // else {
        let filterContent='No Expenses Found' ;
    const filteredExpenses = props.expenses.filter((expense) => {
        return expense.date.getFullYear() == props.onFilterYear;
      });
      if (filteredExpenses.length > 0) {
        const filterContent = filteredExpenses.map((ourExpenses) => (
          <ExpenseItem key={ourExpenses.id} expense={ourExpenses} />
        ));
      }
      
    return(
        <ul className='expenses-list '>{filterContent}</ul>
        
    );
    // }
    
}

export default ExpensesList;
